<script>
	import { goto } from '$app/navigation';
	import { account } from '$lib';
	import '@appwrite.io/pink';
	import '@appwrite.io/pink-icons';

	async function signOut() {
		try {
			await account.deleteSession('current');
		} catch (err) {
			console.log(err);
		} finally {
			goto('/');
		}
	}
</script>

